import * as React from 'react';
import signalRService from '../../../services/signalRService';
import { Row, Col, Comment, List, Form, Image, Button, Card } from 'antd';
import { Comment as IComment } from '../../../services/comment/commentService';
import moment from 'moment';
import TextArea from 'antd/lib/input/TextArea';
import { inject, observer } from 'mobx-react';
import Stores from '../../../stores/storeIdentifier';
import AuthenticationStore from '../../../stores/authenticationStore';
import CommentStore from '../../../stores/commentStore';

interface IConversationBoxProps {
  requestId: string;
  authenticationStore?: AuthenticationStore;
  commentStore?: CommentStore;
}

interface ReplyEditorProps {
  buttonText?: string;
  onValidated: Function;
}

const ReplyEditor = (props: ReplyEditorProps) => {
  const buttonText = props.buttonText ? props.buttonText : 'Add Comment';

  return (
    <>
      <Form onFinish={(values: any) => props.onValidated(values)}>
        <Row gutter={24}>
          <Col span={20}>
            <Form.Item name="content" style={{ margin: 0 }}>
              <TextArea rows={1} />
            </Form.Item>
          </Col>
          <Col span={4}>
            <Form.Item style={{ margin: 0 }}>
              <Button htmlType="submit" style={{ width: '100%' }}>
                {buttonText}
              </Button>
            </Form.Item>
          </Col>
        </Row>
      </Form>
    </>
  );
};

@inject(Stores.AuthenticationStore, Stores.CommentStore)
@observer
class CommentBox extends React.Component<IConversationBoxProps> {
  state = {
    joinedGroup: false,
    replyingId: '',
    loading: true,
  };
  componentDidMount() {
    const { requestId } = this.props;

    this.props.commentStore?.getCommentsOfRequest(requestId).then(() => {
      signalRService.on('commentCreated', (comment) => {
        console.log(comment);
        this.props.commentStore?.addCommentToStore({
          author: {
            firstName: String(comment.author.firstName),
            lastName: String(comment.author.lastName)
          },
          content: comment.content,
          createdAt: comment.createdAt,
          id: comment.id,
          parentId: comment.parentId,
          requestId: comment.requestId
        })
      });
      this.joinGroup(requestId);
    });
  }
  async componentDidUpdate(prevProps: IConversationBoxProps) {
    if (prevProps.requestId != this.props.requestId) {
      this.props.commentStore?.getCommentsOfRequest(this.props.requestId).then(async () => {
        signalRService.on('commentCreated', (comment) => {
          console.log(comment);
        });
        await this.leaveGroup(prevProps.requestId);
        this.joinGroup(this.props.requestId);
      });
    }
  }

  componentWillUnmount() {
    console.log('Unmount');
    this.leaveGroup(this.props.requestId);
    signalRService.off('commentCreated');
  }

  joinGroup = (id: string) => {
    return new Promise((resolve, reject) => {
      signalRService
        .joinGroup(`conversation:${id}`)
        .then(() => {
          this.setState(
            {
              joinedGroup: true,
            },
            () => {
              resolve();
            }
          );
        })
        .catch(reject);
    });
  };

  leaveGroup = (id: string) => {
    return new Promise((resolve, reject) => {
      signalRService
        .leaveGroup(`conversation:${id}`)
        .then(() => {
          this.setState(
            {
              joinedGroup: true,
            },
            async () => {
              resolve();
            }
          );
        })
        .catch(reject);
    });
  };

  handleReply = (comment: IComment) => {
    this.setState({
      replyingId: comment.id,
    });
  };

  renderComment = (comment: IComment, reply: IComment[] = []) => {
    return (
      <>
        <Comment
          actions={
            !comment.parentId
              ? [
                  <span key="comment-nested-reply-to" onClick={() => this.handleReply(comment)}>
                    Reply to
                  </span>,
                ]
              : []
          }
          content={
            <div className="comment">
              <p>{comment.content}</p>
            </div>
          }
          avatar={
            <Image
              width="100%"
              src="error"
              fallback="data:image/png;base64,"
            />
          }
          author={
            <div>
              {comment.author.firstName} {comment.author.lastName}
            </div>
          }
          datetime={<div>{moment(comment.createdAt).format('YYYY-MM-DD HH:mm')}</div>}
          key={comment.id}
        >
          {reply.map((comment) => this.renderComment(comment))}
        </Comment>

        {comment.id === this.state.replyingId ? (
          <div className="comment-reply">
            <Comment
              key="reply-comment"
              avatar={
                <Image
                  width="100%"
                  src="error"
                  fallback="data:image/png;base64,"
                />
              }
              content={
                <ReplyEditor
                  onValidated={async (values: any) => {
                    this.props.commentStore?.createNewComment({
                      content: values.content,
                      parentId: comment.id,
                      requestId: this.props.requestId,
                    });
                  }}
                  buttonText="Reply"
                />
              }
            />
          </div>
        ) : (
          ''
        )}
      </>
    );
  };

  render() {
    const comments = this.props.commentStore?.comments || [];
    console.log(comments);
    return (
      <Card>
        <Row>
          <Col span={24}>
            <Comment
              avatar={
                <Image
                  width="100%"
                  src="error"
                  fallback="data:image/png;base64,"
                />
              }
              author={
                <div>
                  {this.props.authenticationStore?.user?.firstName} {this.props.authenticationStore?.user?.lastName}
                </div>
              }
              content={
                <ReplyEditor
                  onValidated={async (values: any) => {
                    await this.props.commentStore?.createNewComment({
                      content: values.content,
                      requestId: this.props.requestId,
                    });
                  }}
                />
              }
            />
          </Col>
        </Row>
        <Row>
          <Col span={24}>
            <List
              className="comment-list"
              rowKey="id"
              itemLayout="horizontal"
              dataSource={comments.filter((c) => !c.parentId || c.parentId === '')}
              renderItem={(comment: IComment) => {
                const reply = comments.filter((c) => c.parentId == comment.id);
                return <li key={comment.id}>{this.renderComment(comment, reply)}</li>;
              }}
            />
            ,
          </Col>
        </Row>
      </Card>
    );
  }
}

export default CommentBox;
