import * as React from 'react';
import signalRService from '../../../services/signalRService';
import { Row, Col, Comment, List, Form, Image, Button, Card, Input, Space, Typography, Radio } from 'antd';
import { Comment as IComment } from '../../../services/comment/commentService';
import moment from 'moment';
import { inject, observer } from 'mobx-react';
import Stores from '../../../stores/storeIdentifier';
import AuthenticationStore from '../../../stores/authenticationStore';
import CommentStore from '../../../stores/commentStore';
import { FormInstance } from 'antd/lib/form';

interface IConversationBoxProps {
  requestId: string;
  authenticationStore?: AuthenticationStore;
  commentStore?: CommentStore;
}

interface ReplyEditorProps {
  buttonText?: string;
  onValidated: Function;
}


const { TextArea } = Input;
const { Title } = Typography;

class ReplyEditor extends React.Component<ReplyEditorProps> {
  formRef = React.createRef<FormInstance>();

  render() {
    const buttonText = this.props.buttonText ? this.props.buttonText : 'Add Comment';

    return (
      <>
        <Form
          ref={this.formRef}
          onFinish={async (values: any) => {
            try {
              await this.props.onValidated(values);
              this.formRef.current?.setFieldsValue({
                content: '',
              });
            } catch (e) {
              this.formRef.current?.setFieldsValue({
                content: '',
              });
              throw e;
            }
          }}
        >
          <Space size={16} direction={'vertical'} style={{ width: '100%' }}>
            <Form.Item name="content" style={{ margin: 0 }}>
              <TextArea rows={3} style={{ width: '100%' }} />
            </Form.Item>
            <Form.Item style={{ margin: 0 }}>
              <Button htmlType="submit">{buttonText}</Button>
            </Form.Item>
          </Space>
        </Form>
      </>
    );
  }
}

type ChronoOrder = 'newest' | 'oldest';

interface CommentBoxState {
  joinedGroup: boolean;
  replyingId: string;
  loading: boolean;
  chronoOrder: ChronoOrder;
  order: string;
}


@inject(Stores.AuthenticationStore, Stores.CommentStore)
@observer
class CommentBox extends React.Component<IConversationBoxProps> {
  buttonRef = React.createRef<any>();
  state: CommentBoxState = {
    joinedGroup: false,
    replyingId: '',
    loading: true,
    chronoOrder: 'newest',
    order: 'asc',
  };

  componentDidMount() {
    const { requestId } = this.props;

    this.props.commentStore?.getCommentsOfRequest(requestId, this.state.order).then(() => {
      signalRService.on('commentDeleted', (comment) => {
        this.props.commentStore?.deletedComment({ id: comment.id });
      });
      signalRService.on('commentCreated', (comment) => {
        //
        if (!this.buttonRef.current.state.checked) {
          this.props.commentStore?.addCommentToStoreAfter({
            author: {
              firstName: String(comment.author.firstName),
              lastName: String(comment.author.lastName),
              avatar: String(comment.author.avatar)
            },
            content: comment.content,
            createdAt: comment.createdAt,
            id: comment.id,
            parentId: comment.parentId,
            requestId: comment.requestId,
          });
        } else {
          this.props.commentStore?.addCommentToStoreBefore({
            author: {
              firstName: String(comment.author.firstName),
              lastName: String(comment.author.lastName),
              avatar: String(comment.author.avatar)
            },
            content: comment.content,
            createdAt: comment.createdAt,
            id: comment.id,
            parentId: comment.parentId,
            requestId: comment.requestId,
          });
        }
      });

      this.joinGroup(requestId);
    });
  }
  async componentDidUpdate(prevProps: IConversationBoxProps) {
    //const { requestId } = this.props;
    
    if (prevProps.requestId != this.props.requestId) {
      this.props.commentStore?.getCommentsOfRequest(this.props.requestId, this.state.order).then(async () => {
        await this.leaveGroup(prevProps.requestId);
        await this.joinGroup(this.props.requestId);
      });
    }
  }

  componentWillUnmount() {
    
    this.leaveGroup(this.props.requestId);
    signalRService.off('commentCreated');
    signalRService.off('commentDeleted');
  }

  joinGroup = (id: string) => {
    
    return new Promise((resolve, reject) => {
      signalRService
        .joinGroup(`conversation:${id}`)
        .then((res) => {
          this.setState(
            {
              joinedGroup: true,
            },
            () => {
              resolve(res);
            }
          );
        })
        .catch(reject);
    });
  };

  leaveGroup = (id: string) => {
    return signalRService
        .leaveGroup(`conversation:${id}`)
  };

  handleReply = (comment: IComment) => {
    this.setState({
      replyingId: comment.id,
    });
  };

  handleDelete = (comment: IComment) => {
    this.props.commentStore?.deleteCommentofRequest(comment, this.props.requestId).then((res) => {
      
      if (res.status === 200) {
        //this.props.commentStore?.getCommentsOfRequest(this.props.requestId, this.state.order);
      } else {
        alert('Can not delete this comment');
      }
    });
    //
  };

  renderComment = (comment: IComment, reply: IComment[] = []) => {
    return (
      <Comment
        actions={
          !comment.parentId
            ? [
                <span key="comment-nested-reply-to" onClick={() => this.handleReply(comment)}>
                  Reply
                </span>,
                <span key="comment-nested-reply-to" onClick={() => this.handleDelete(comment)}>
                  Delete
                </span>,
              ]
            : [<span key="comment-nested-reply-to" onClick={() => this.handleDelete(comment)}>
                  Delete
                </span>]
        }
        content={
          <div className="comment">
            <p>{comment.content}</p>
          </div>
        }
        avatar={
          <Image
            width="100%"
            src="error"
            fallback="data:image/png;base64,"
          />
        }
        author={
          <span style={{ fontWeight: 700 }}>
            {comment.author.firstName} {comment.author.lastName}
          </span>
        }
        datetime={<>{moment.utc(comment.createdAt).fromNow()}</>}
        key={comment.id}
      >
        {reply.map((comment) => this.renderComment(comment))}
        {comment.id === this.state.replyingId ? (
          <div className="comment__reply">
            <Comment
              key="reply-comment"
              className="reply-input"
              avatar={
                <Image
                  width="100%"
                  src="error"
                  fallback="data:image/png;base64,"
                />
              }
              content={
                <ReplyEditor
                  onValidated={async (values: any) => {
                    if(values){
                      alert('You have not enter message');
                      return;
                    }
                    else if (values.content.length <= 0) {
                      alert('You have not enter message');
                    } else {
                      this.props.commentStore?.createNewComment({
                        content: values.content,
                        parentId: comment.id,
                        requestId: this.props.requestId,
                      });
                    }
                  }}
                  buttonText="Reply"
                />
              }
            />
          </div>
        ) : (
          ''
        )}
      </Comment>
    );
  };

  render() {
    const comments = this.props.commentStore?.comments || [];
    return (
      <Card>
        <Row>
          <Col span={24} style={{ textAlign: 'right' }}>
            <Radio.Group
              onChange={(e: any) => {
                this.props.commentStore?.getCommentsOfRequest(this.props.requestId, e.target.value);
              }}
              defaultValue="asc"
            >
              <Radio.Button defaultChecked ref={this.buttonRef} value="desc">
                Newest First
              </Radio.Button>
              <Radio.Button value="asc">Oldest First</Radio.Button>
            </Radio.Group>
          </Col>
        </Row>
        <Row>
          <Col span={24}>
            <Comment
              className="reply-input"
              avatar={
                <Image
                  width="100%"
                  src="error"
                  fallback="data:image/png;base64,"
                />
              }
              author={
                <Title level={5}>
                  {this.props.authenticationStore?.user?.firstName} {this.props.authenticationStore?.user?.lastName}
                </Title>
              }
              content={
                <ReplyEditor
                  onValidated={async (values: any) => {
                    console.log(values);
                    if(values){
                      alert('You have not enter message');
                      return;
                    }
                    else  if (values  && values.content.length <= 0) {
                      alert('You have not enter message');
                    } else {
                      await this.props.commentStore?.createNewComment({
                        content: values.content,
                        requestId: this.props.requestId,
                      });
                    }
                  }}
                />
              }
            />
          </Col>
        </Row>
        <Row>
          <Col span={24}>
            <List
              className="comment-list"
              rowKey="id"
              itemLayout="horizontal"
              dataSource={comments.filter((c) => !c.parentId || c.parentId === '')}
              renderItem={(comment: IComment) => {
                const reply = comments.filter((c) => c.parentId == comment.id);
                return <li key={comment.id}>{this.renderComment(comment, reply)}</li>;
              }}
            />
          </Col>
        </Row>
      </Card>
    );
  }
}

export default CommentBox;
